package Test;

public class TypesOfOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b = 65;
		System.out.println(b++);
		System.out.println(b);
		System.out.println(++b);
		
		System.out.println("#########");
		
		int a=50; 
		System.out.println(a--);
		System.out.println(a);
		System.out.println(--a);
	}	

}
