package Test;

public class VerifyNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Number = -9;

		if (Number >= 0) {

			System.out.println("Number is Positive: " + Number);
		}
		if (Number < 0) {

			System.out.println("Number is Negative: " + Number);
		}
	}

}
